FROM company
WHERE com_id = #{com_id}

SELECT pre.pre_num, pre.menu_idx, menu.menu_name, menu_price 
FROM pre_order pre , menu
WHERE res_idx = #{res_idx} AND pre.menu_idx = menu.menu_idx;

