mapper.select:
SELECT com_name
FROM company
WHERE com_id = #{com_id}

SELECT COUNT(*)
FROM review
WHERE com_id = #{com_id}

	SELECT ROUND(AVG(review_score)/2,2)
	FROM review
    WHERE com_id = #{com_id}

SELECT * 
FROM review
WHERE com_id = #{com_id}
ORDER BY review_update DESC;

mapper.update:
